<style>
  #re-panel-header-wrapper {
    display: flex;
    align-items: center;
    justify-content: space-between;
    height: 40px;
    width: 100%;
    background-color: var(--e-a-dark-bg);
    color: var(--e-a-color-white);
  }

  #re-panel-header-title {
    color: var(--e-a-color-white);
    flex: 1;
    font-size: 15px;
    font-weight: 700;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0;
    margin: 0;
  }

  .re-header-button {
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    border: 0;
    color: currentColor;
    background-color: transparent;
  }

  .re-header-button:hover {
    color: var(--e-a-dark-color-txt-hover);
    cursor: pointer;
  }
</style>
